mapper.select:
select b.course_time_id,b.cla_id,b.cla_date,WEEKDAY(b.cla_date) + 1 as week_day, hour(b.start_time) as start_hour,b.start_time, b.end_time,b.source,b.status,b.memo,b.status,b.class_theme, a.cla_name,a.cla_color,course.course_name, s.staff_id as teacher_id,s.staff_name, r.room_id,r.room_name, (select count(distinct student_id) from sc_student_course sc where sc.cla_id=a.cla_id and sc.status='1') as student_count, b.pay_hour from sc_course_cla a left join sc_course course on a.course_id=course.course_id ,sc_cla_time b left join sys_staff s on b.teacher_id = s.staff_id left join sc_room r on b.room_id = r.room_id where a.cla_id=b.cla_id
and b.course_time_id=#{courseTimeId}
and b.cla_date between #{beginDate} and #{endDate}
and a.cla_id=#{claId}
and exists(select 1 from sc_student_course sc where a.cla_id=sc.cla_id and sc.student_id=#{studentId})
and a.depart_id=#{deptId}
and exists(select 1 from sys_user_dept ud where ud.user_id=#{userId} and (ud.dept_id=a.depart_id or ud.dept_id=-1))
and b.teacher_id=#{teacherId}
and a.course_id = #{courseId}
and b.status = '1'
and b.status = '2'
order by b.cla_date,b.status,b.start_time

select ct.course_time_id,ct.cla_id,cc.cla_name, course.course_id,course.course_name, ct.teacher_id,s.staff_name, ct.status, ct.cla_date,ct.start_time,ct.end_time, ct.real_cla_date,ct.real_start_time,ct.real_end_time, ct.need_attend_cnt,ct.real_attend_cnt,ct.leave_cnt,ct.out_cnt, ct.pay_hour,ct.pay_total_hour, ct.pay_total_fee, ct.room_name,ct.class_theme,ct.memo, (select u.name from sys_user u where u.user_id=ct.last_update_user) as last_update_user_name, ct.create_time,ct.last_update_time from sc_course_cla cc left join sc_course course on cc.course_id=course.course_id, sc_cla_time ct left join sys_staff s on ct.teacher_id=s.staff_id where cc.cla_id = ct.cla_id
and ct.cla_date between #{searchClaTime.beginDate} and #{searchClaTime.endDate}
and ct.cla_id=#{searchClaTime.claId}
and cc.depart_id=#{searchClaTime.deptId}
and exists(select 1 from sys_user_dept ud where ud.user_id=#{searchClaTime.userId} and (ud.dept_id=cc.depart_id or ud.dept_id=-1))
and ct.teacher_id=#{searchClaTime.teacherId}
and cc.course_id = #{searchClaTime.courseId}
and ct.status = '1'
and ct.status = '2'
order by ct.status,ct.real_cla_date desc,ct.cla_date desc,ct.start_time
order by ct.real_cla_date desc,ct.last_update_time desc

select count(1) from sc_course_cla b, sc_cla_time a
where a.cla_id=b.cla_id
and a.cla_date between #{beginDate} and #{endDate}

    and a.status='2'


    and a.status='1'


    and a.teacher_id=#{teacherId}


    and b.depart_id=#{deptId}


    and exists(select 1 from sys_user_dept ud
        where ud.user_id=#{userId}
          and (ud.dept_id=b.depart_id or ud.dept_id=-1))
