mapper.select:
select a.order_id,a.order_type,a.actual_total_fee,a.receipt_fee,a.balance_fee, (select s.staff_name from sys_staff s where s.staff_id=a.sale_staff_id) sale_staff_name, a.order_tag,a.handle_dept_name,a.handle_date, (select u.name from sys_user u where user_id=a.create_user) as create_user_name, a.memo, b.student_name,b.phone, (select group_concat(od.course_name,'(',d.dept_name,')',',',od.charge_name,' 数量:',od.buy_count separator ';') from sc_order_detail od left join sys_dept d on od.dept_id=d.dept_id where a.order_id=od.order_id) as order_detail, a.order_status from sc_order a left join sc_student b on a.student_id=b.student_id where 1=1
and a.student_id = #{reqSearchScOrder.studentId}
and a.handle_date between #{reqSearchScOrder.handleDateBegin} and #{reqSearchScOrder.handleDateEnd}
and actual_total_fee > balance_fee + receipt_fee
and actual_total_fee > balance_fee + receipt_fee
and actual_total_fee = balance_fee + receipt_fee
and exists(select 1 from sc_order_detail d where a.order_id=d.order_id and d.dept_id=#{reqSearchScOrder.deptId} )
and exists(select 1 from sc_order_detail d where a.order_id=d.order_id and d.course_id=#{reqSearchScOrder.courseId} )
and a.sale_staff_id = #{reqSearchScOrder.saleStaffId}
and a.order_type = #{reqSearchScOrder.orderType}
and a.order_status = #{reqSearchScOrder.orderStatus}
and a.create_user = #{reqSearchScOrder.createUser}
and a.sale_source_tag = #{reqSearchScOrder.saleSourceTag}
order by a.create_time desc
